package week1.day3;

public class PrimeNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int input=13;
int output=0;
for (int i=1;i<=input;i++) {
	if(input%i==0);
	output++;
}
if (output==2) {
System.out.println("it is a primenumber");
}
else {
	System.out.println("it is not a primenumber");
}
	}

}
